    @Test
    public void normal() {
        // The seller public key to set document.
        String sellerPrivateKey = "privC15YAp4M4oDLcJ6JqyPqYH55VXPeNckE2AVqWcHN8BB4PAVwGjJr";
        // The seller address.
        String sellerAddress = "buQfTPaQBzFGBzGy87pSsc6MmNJKKKmzTSyC";
        // The document id.
        String documentId = "1";
        // The document name.
        String documentName = "BUMO白皮书";
        // The document url.
        String documentUrl = "https://BUMO.io/BUMO-Technology-White-Paper-cn";
        // The document hash type.
        String documentHashType = "md5";
        // The document hash.
        String documentHash = "31be016368639ba1a7ae7b63247807a1";

        // Setting the document.
        Atp60TokenDemo atp60TokenDemo = new Atp60TokenDemo();
        String txHash = atp60TokenDemo.setDocumentTx(sellerPrivateKey, sellerAddress, documentId, documentName, documentUrl, documentHashType, documentHash);

        // Check Tx status.
        JSONObject result = atp60TokenDemo.getTxStatus(txHash);
        System.out.println(JSON.toJSONString(result, true));
        assertEquals(result.getString("desc"), 0, result.getIntValue("code"));
    }

